import React, { useEffect, useMemo, useState } from 'react';
import styled from 'styled-components';
import conveniosService from '../services/conveniosService';

const Container = styled.div`
  padding: 1rem 0;
`;

const Title = styled.h1`
  font-size: 1.6rem;
  color: #333;
  margin-bottom: 1rem;
`;

const Controls = styled.div`
  display: flex;
  gap: 12px;
  align-items: center;
  margin-bottom: 1rem;
`;

const SearchInput = styled.input`
  padding: 0.6rem 0.8rem;
  border: 2px solid #e1e5e9;
  border-radius: 8px;
  min-width: 280px;
`;

const CardGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, minmax(0, 1fr));
  gap: 16px;

  @media (max-width: 1200px) {
    grid-template-columns: repeat(2, minmax(0, 1fr));
  }

  @media (max-width: 700px) {
    grid-template-columns: 1fr;
  }
`;

const Card = styled.div`
  background: white;
  border-radius: 12px;
  padding: 16px;
  box-shadow: 0 5px 20px rgba(0, 0, 0, 0.08);
`;

const Convenios = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [search, setSearch] = useState('');

  useEffect(() => {
    let cancelled = false;
    async function load() {
      setLoading(true);
      setError('');
      try {
        const data = await conveniosService.list();
        if (!cancelled) setItems(Array.isArray(data) ? data : []);
      } catch (err) {
        if (!cancelled) setError(err.message || 'Erro ao carregar convênios');
      } finally {
        if (!cancelled) setLoading(false);
      }
    }
    load();
    return () => { cancelled = true; };
  }, []);

  const filtered = useMemo(() => {
    const q = search.trim().toLowerCase();
    if (!q) return items;
    return items.filter(e => String(e.nome_convenio || '').toLowerCase().includes(q));
  }, [items, search]);

  return (
    <Container>
      <Title>Convênios</Title>
      {error && <div style={{ background: '#fee', color: '#c33', padding: '8px 10px', borderRadius: 8, marginBottom: 10 }}>{error}</div>}

      <Controls>
        <SearchInput placeholder="Buscar por nome" value={search} onChange={e => setSearch(e.target.value)} />
      </Controls>

      {loading ? (
        <div>Carregando...</div>
      ) : filtered.length === 0 ? (
        <div>Nenhum convênio encontrado.</div>
      ) : (
        <CardGrid>
          {filtered.map(c => (
            <Card key={c.id_convenio}>{c.nome_convenio}</Card>
          ))}
        </CardGrid>
      )}
    </Container>
  );
};

export default Convenios;
