import React, { useEffect, useMemo, useState } from 'react';
import styled from 'styled-components';
import especialistasService from '../services/especialistasService';

const Container = styled.div`
  padding: 1rem 0;
`;

const Title = styled.h1`
  font-size: 1.6rem;
  color: #333;
  margin-bottom: 1rem;
`;

const Controls = styled.div`
  display: flex;
  gap: 12px;
  align-items: center;
  margin-bottom: 1rem;
`;

const SearchInput = styled.input`
  padding: 0.6rem 0.8rem;
  border: 2px solid #e1e5e9;
  border-radius: 8px;
  min-width: 280px;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  background: white;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 5px 20px rgba(0, 0, 0, 0.08);
`;

const Th = styled.th`
  text-align: left;
  padding: 12px 14px;
  background: #f9fafb;
  color: #374151;
  border-bottom: 1px solid #f0f0f0;
`;

const Td = styled.td`
  padding: 12px 14px;
  border-bottom: 1px solid #f5f5f5;
  color: #4b5563;
`;

const Badge = styled.span`
  display: inline-block;
  padding: 2px 8px;
  border-radius: 999px;
  font-size: 12px;
  font-weight: 600;
  color: ${p => (p.$ok ? '#065f46' : '#7c2d12')};
  background: ${p => (p.$ok ? '#d1fae5' : '#fee2e2')};
`;

const Empty = styled.div`
  padding: 1rem;
  color: #6b7280;
`;

const ErrorBox = styled.div`
  background: #fee;
  color: #c33;
  padding: 0.8rem;
  border-radius: 8px;
  font-size: 0.9rem;
  margin-bottom: 1rem;
  border: 1px solid #fcc;
`;

function parseHorario(horario_atendimento) {
  if (!horario_atendimento) return '-';
  try {
    const raw = typeof horario_atendimento === 'string' ? JSON.parse(horario_atendimento) : horario_atendimento;
    if (Array.isArray(raw)) {
      return raw.map(h => `${h.inicio}-${h.fim}`).join(', ');
    }
    return String(horario_atendimento);
  } catch (_) {
    return String(horario_atendimento);
  }
}

const Especialistas = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [search, setSearch] = useState('');

  useEffect(() => {
    let cancelled = false;
    async function load() {
      setLoading(true);
      setError('');
      try {
        const data = await especialistasService.list();
        if (!cancelled) setItems(Array.isArray(data) ? data : []);
      } catch (err) {
        if (!cancelled) setError(err.message || 'Erro ao carregar especialistas');
      } finally {
        if (!cancelled) setLoading(false);
      }
    }
    load();
    return () => { cancelled = true; };
  }, []);

  const filtered = useMemo(() => {
    const q = search.trim().toLowerCase();
    if (!q) return items;
    return items.filter(e =>
      String(e.nome_especialista || '').toLowerCase().includes(q) ||
      String(e.descricao || '').toLowerCase().includes(q)
    );
  }, [items, search]);

  return (
    <Container>
      <Title>Especialistas</Title>

      {error && <ErrorBox>{error}</ErrorBox>}

      <Controls>
        <SearchInput
          placeholder="Buscar por nome ou descriÃ§Ã£o"
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
      </Controls>

      {loading ? (
        <Empty>Carregando...</Empty>
      ) : filtered.length === 0 ? (
        <Empty>Nenhum especialista encontrado.</Empty>
      ) : (
        <Table>
          <thead>
            <tr>
              <Th>ID</Th>
              <Th>Nome</Th>
              <Th>DescriÃ§Ã£o</Th>
              <Th>HorÃ¡rio</Th>
              <Th>Valor</Th>
              <Th>ConvÃªnio</Th>
              <Th>DuraÃ§Ã£o</Th>
              <Th>Ger. Agenda</Th>
            </tr>
          </thead>
          <tbody>
            {filtered.map(e => (
              <tr key={e.id_especialista}>
                <Td>{e.id_especialista}</Td>
                <Td>{e.nome_especialista}</Td>
                <Td>{e.descricao || '-'}</Td>
                <Td>{parseHorario(e.horario_atendimento)}</Td>
                <Td>{e.valor_consulta || '-'}</Td>
                <Td>
                  <Badge $ok={e.aceita_convenio === true}>{e.aceita_convenio ? 'Sim' : 'NÃ£o'}</Badge>
                </Td>
                <Td>{e.tempo_consulta ? `${e.tempo_consulta} min` : '-'}</Td>
                <Td>
                  <Badge $ok={e.gerenciar_agenda === true}>{e.gerenciar_agenda ? 'Ativo' : 'Inativo'}</Badge>
                </Td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </Container>
  );
};

export default Especialistas;
