  try {
    const raw = typeof horario_atendimento === 'string' ? JSON.parse(horario_atendimento) : horario_atendimento;
    const days = [['seg', 'Seg'], ['ter', 'Ter'], ['qua', 'Qua'], ['qui', 'Qui'], ['sex', 'Sex'], ['sab', 'Sáb'], ['dom', 'Dom']];
    if (Array.isArray(raw)) {
