      return value ? [{
        label: 'Seg–Dom',
        value
      }] : [];
    }
    if (raw && typeof raw === 'object') {
      const lines = [];
      for (const [key, label] of days) {
        const arr = raw[key] || [];
        if (Array.isArray(arr) && arr.length > 0) {
          const value = arr.map(h => `${h.inicio}–${h.fim}`).join(', ');
          lines.push({
            label,
            value
          });
        }
      }
      return lines;
    }
    return [];
  } catch (_) {
    return [];
  }
}

