import React, { useState } from 'react';
import styled from 'styled-components';
import logoLight from '../assets/logo-light.svg';
import logoDark from '../assets/logo-dark.svg';
import { useThemeCustom } from '../contexts/ThemeContext';
import authService from '../services/authService';
import { useStackNav } from '../contexts/StackNav';

const SidebarContainer = styled.aside`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: #ffffff;
  width: ${props => (props.$collapsed ? '72px' : '240px')};
  transition: width 0.25s ease;
  height: 100vh;
  position: sticky;
  top: 0;
  overflow: hidden;
  border-right: 0;
`;

const ToggleButton = styled.button`
  width: 100%;
  background: transparent;
  color: rgba(255,255,255,0.85);
  padding: 12px 16px;
  border-bottom: 1px solid rgba(255,255,255,0.15);
  cursor: pointer;
  text-align: left;
  &:hover { color: #ffffff; }
`;

const Menu = styled.ul`
  list-style: none;
  margin: 0;
  padding: 8px 8px;
`;

const MenuItem = styled.li`
  margin: 4px 0;
`;

const ItemButton = styled.button`
  width: 100%;
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 10px 12px;
  border-radius: 8px;
  color: ${props => (props.$active ? '#ffffff' : 'rgba(255,255,255,0.85)')};
  background: ${props => (props.$active ? 'rgba(255,255,255,0.22)' : 'transparent')};
  cursor: pointer;
  &:hover { background: ${props => (props.$active ? 'rgba(255,255,255,0.22)' : 'rgba(255,255,255,0.12)')}; }
`;

const Icon = styled.span`
  font-size: 18px;
  width: 24px;
  text-align: center;
`;

const Label = styled.span`
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const Sidebar = () => {
  const { currentPage, goToPage } = useStackNav();
  const { theme } = useThemeCustom();
  const [collapsed, setCollapsed] = useState(false);

  const items = [
    { key: 'dashboard', label: 'Dashboard', icon: 'üìä' },
    { key: 'agendamento', label: 'Agendamento', icon: 'üóìÔ∏è' },
    { key: 'cliente', label: 'Clientes', icon: 'üßë' },
    { key: 'especialistas', label: 'Especialistas', icon: 'üë©‚Äç‚öïÔ∏è' },
    { key: 'especialidades', label: 'Especialidades', icon: 'üè∑Ô∏è' },
    { key: 'convenios', label: 'Conv√™nios', icon: 'üìÑ' },
    { key: 'empresa', label: 'Empresa', icon: 'üè¢' },
    { key: 'configuracoes', label: 'Configura√ß√µes', icon: '‚öôÔ∏è' }
  ];

  return (
    <SidebarContainer $collapsed={collapsed}>
      <div style={{ display: 'flex', alignItems: 'center', gap: 10, padding: '12px 16px', borderBottom: '1px solid rgba(255,255,255,0.15)' }}>
        <img
          src={theme === 'dark' ? logoDark : logoLight}
          alt="Dualm"
          style={{ maxWidth: '100%', height: 'auto', maxHeight: 28, display: 'block' }}
        />
        <div style={{ marginLeft: 'auto' }}>
          <button onClick={() => { authService.logout(); goToPage('login'); }} title="Sair" style={{ background: 'rgba(255,255,255,0.2)', color: '#ffffff', padding: '6px 10px', borderRadius: 8 }}>Sair</button>
        </div>
      </div>
      <ToggleButton onClick={() => setCollapsed(v => !v)}>
        {collapsed ? '‚û§' : '‚óÄ Recolher'}
      </ToggleButton>
      <Menu>
        {items.map(item => (
          <MenuItem key={item.key}>
            <ItemButton
              onClick={() => goToPage(item.key)}
              $active={currentPage === item.key}
              title={item.label}
            >
              <Icon aria-hidden>{item.icon}</Icon>
              {!collapsed && <Label>{item.label}</Label>}
            </ItemButton>
          </MenuItem>
        ))}
      </Menu>
    </SidebarContainer>
  );
};

export default Sidebar;
