import React, { useEffect, useMemo, useState } from 'react';
import styled from 'styled-components';
import especialistasService from '../services/especialistasService';

const Container = styled.div`
  padding: 1rem 0;
`;

const Title = styled.h1`
  font-size: 1.6rem;
  color: #333;
  margin-bottom: 1rem;
`;

const Controls = styled.div`
  display: flex;
  gap: 12px;
  align-items: center;
  margin-bottom: 1rem;
`;

const SearchInput = styled.input`
  padding: 0.6rem 0.8rem;
  border: 2px solid #e1e5e9;
  border-radius: 8px;
  min-width: 280px;
`;

const PrimaryButton = styled.button`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  padding: 0.6rem 0.9rem;
  border-radius: 8px;
  font-weight: 600;
  white-space: nowrap;
`;

const CardGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, minmax(0, 1fr));
  gap: 16px;

  @media (max-width: 1200px) {
    grid-template-columns: repeat(2, minmax(0, 1fr));
  }

  @media (max-width: 700px) {
    grid-template-columns: 1fr;
  }
`;

const Card = styled.div`
  background: white;
  border-radius: 12px;
  padding: 16px;
  box-shadow: 0 5px 20px rgba(0, 0, 0, 0.08);
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const CardHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
`;

const CardTitle = styled.h3`
  margin: 0;
  color: #1f2937;
  font-size: 1.1rem;
`;

const Actions = styled.div`
  margin-left: auto;
  display: inline-flex;
  gap: 6px;
`;

const IconButton = styled.button`
  background: #f3f4f6;
  color: #374151;
  padding: 6px 8px;
  border-radius: 8px;
  font-size: 14px;
  line-height: 1;
  &:hover { background: #e5e7eb; }
`;

const CardDesc = styled.p`
  margin: 0;
  color: #6b7280;
  font-size: 0.95rem;
`;

const MetaRow = styled.div`
  display: grid;
  grid-template-columns: repeat(2, minmax(0, 1fr));
  gap: 8px 12px;
`;

const MetaItem = styled.div`
  color: #4b5563;
  font-size: 0.9rem;
`;

const MetaLabel = styled.span`
  color: #6b7280;
`;

const Badge = styled.span`
  display: inline-block;
  padding: 2px 8px;
  border-radius: 999px;
  font-size: 12px;
  font-weight: 600;
  color: ${p => (p.$ok ? '#065f46' : '#7c2d12')};
  background: ${p => (p.$ok ? '#d1fae5' : '#fee2e2')};
`;

const Empty = styled.div`
  padding: 1rem;
  color: #6b7280;
`;

const ErrorBox = styled.div`
  background: #fee;
  color: #c33;
  padding: 0.8rem;
  border-radius: 8px;
  font-size: 0.9rem;
  margin-bottom: 1rem;
  border: 1px solid #fcc;
`;

const ModalOverlay = styled.div`
  position: fixed;
  inset: 0;
  background: rgba(0,0,0,0.4);
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ModalCard = styled.div`
  width: 100%;
  max-width: 560px;
  background: white;
  border-radius: 12px;
  box-shadow: 0 10px 40px rgba(0,0,0,0.2);
  padding: 16px;
`;

const ModalTitle = styled.h3`
  margin: 0 0 12px 0;
`;

const Form = styled.form`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 12px;
`;

const Field = styled.div`
  display: flex;
  flex-direction: column;
  gap: 6px;
  grid-column: ${p => p.$full ? '1 / -1' : 'auto'};
`;

const Label = styled.label`
  color: #374151;
  font-size: 0.9rem;
`;

const Input = styled.input`
  padding: 10px 12px;
  border: 2px solid #e5e7eb;
  border-radius: 8px;
`;

const TextArea = styled.textarea`
  padding: 10px 12px;
  border: 2px solid #e5e7eb;
  border-radius: 8px;
  min-height: 100px;
`;

const ModalActions = styled.div`
  display: flex;
  justify-content: flex-end;
  gap: 10px;
  margin-top: 12px;
`;

const SecondaryButton = styled.button`
  background: #f3f4f6;
  color: #374151;
  padding: 0.6rem 0.9rem;
  border-radius: 8px;
`;

function parseHorario(horario_atendimento) {
  if (!horario_atendimento) return '-';
  try {
    const raw = typeof horario_atendimento === 'string' ? JSON.parse(horario_atendimento) : horario_atendimento;
    if (Array.isArray(raw)) {
      return raw.map(h => `${h.inicio}-${h.fim}`).join(', ');
    }
    return String(horario_atendimento);
  } catch (_) {
    return String(horario_atendimento);
  }
}

const Especialistas = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [search, setSearch] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [saving, setSaving] = useState(false);
  const [form, setForm] = useState({
    nome_especialista: '',
    descricao: '',
    // formato por dia: { seg:[{inicio,fim}], ter:[...], ... }
    horario_atendimento: {
      seg: [], ter: [], qua: [], qui: [], sex: [], sab: [], dom: []
    },
    valor_consulta: '',
    aceita_convenio: false,
    tempo_consulta: '',
    gerenciar_agenda: true,
  });

  useEffect(() => {
    let cancelled = false;
    async function load() {
      setLoading(true);
      setError('');
      try {
        const data = await especialistasService.list();
        if (!cancelled) setItems(Array.isArray(data) ? data : []);
      } catch (err) {
        if (!cancelled) setError(err.message || 'Erro ao carregar especialistas');
      } finally {
        if (!cancelled) setLoading(false);
      }
    }
    load();
    return () => { cancelled = true; };
  }, []);

  const filtered = useMemo(() => {
    const q = search.trim().toLowerCase();
    if (!q) return items;
    return items.filter(e =>
      String(e.nome_especialista || '').toLowerCase().includes(q) ||
      String(e.descricao || '').toLowerCase().includes(q)
    );
  }, [items, search]);

  const handleCreate = () => {
    setForm({
      nome_especialista: '',
      descricao: '',
      horario_atendimento: { seg: [], ter: [], qua: [], qui: [], sex: [], sab: [], dom: [] },
      valor_consulta: '',
      aceita_convenio: false,
      tempo_consulta: '',
      gerenciar_agenda: true,
    });
    setIsModalOpen(true);
  };

  const handleEdit = (esp) => {
    alert(`Editar especialista: ${esp.nome_especialista}`);
  };

  const handleSubmitCreate = async (e) => {
    e.preventDefault();
    setSaving(true);
    setError('');
    try {
      const payload = { ...form, horario_atendimento: JSON.stringify(form.horario_atendimento) };
      await especialistasService.create(payload);
      // reload list
      const data = await especialistasService.list();
      setItems(Array.isArray(data) ? data : []);
      setIsModalOpen(false);
    } catch (err) {
      setError(err.message || 'Erro ao cadastrar especialista');
    } finally {
      setSaving(false);
    }
  };

  const handleDelete = (esp) => {
    const ok = window.confirm(`Excluir especialista "${esp.nome_especialista}"?`);
    if (ok) {
      alert('Exclus√£o: em breve');
    }
  };

  return (
    <Container>
      <Title>Especialistas</Title>

      {error && <ErrorBox>{error}</ErrorBox>}

      <Controls>
        <SearchInput
          placeholder="Buscar por nome ou descri√ß√£o"
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
        <PrimaryButton onClick={handleCreate}>Cadastrar especialista</PrimaryButton>
      </Controls>

      {loading ? (
        <Empty>Carregando...</Empty>
      ) : filtered.length === 0 ? (
        <Empty>Nenhum especialista encontrado.</Empty>
      ) : (
        <CardGrid>
          {filtered.map(e => (
            <Card key={e.id_especialista}>
              <CardHeader>
                <CardTitle>{e.nome_especialista}</CardTitle>
                <Actions>
                  <IconButton title="Editar" onClick={() => handleEdit(e)}>‚úèÔ∏è</IconButton>
                  <IconButton title="Excluir" onClick={() => handleDelete(e)}>üóëÔ∏è</IconButton>
                </Actions>
              </CardHeader>
              <CardDesc>{e.descricao || 'Sem descri√ß√£o'}</CardDesc>
              <MetaRow>
                <MetaItem>
                  <MetaLabel>Hor√°rio: </MetaLabel>
                  {parseHorario(e.horario_atendimento)}
                </MetaItem>
                <MetaItem>
                  <MetaLabel>Valor: </MetaLabel>
                  {e.valor_consulta || '-'}
                </MetaItem>
                <MetaItem>
                  <MetaLabel>Conv√™nio: </MetaLabel>
                  <Badge $ok={e.aceita_convenio === true}>{e.aceita_convenio ? 'Sim' : 'N√£o'}</Badge>
                </MetaItem>
                <MetaItem>
                  <MetaLabel>Dura√ß√£o: </MetaLabel>
                  {e.tempo_consulta ? `${e.tempo_consulta} min` : '-'}
                </MetaItem>
                <MetaItem>
                  <MetaLabel>Ger. agenda: </MetaLabel>
                  <Badge $ok={e.gerenciar_agenda === true}>{e.gerenciar_agenda ? 'Ativo' : 'Inativo'}</Badge>
                </MetaItem>
              </MetaRow>
            </Card>
          ))}
        </CardGrid>
      )}

      {isModalOpen && (
        <ModalOverlay>
          <ModalCard>
            <ModalTitle>Novo especialista</ModalTitle>
            <Form onSubmit={handleSubmitCreate}>
              <Field $full>
                <Label>Nome *</Label>
                <Input
                  value={form.nome_especialista}
                  onChange={e => setForm({ ...form, nome_especialista: e.target.value })}
                  required
                />
              </Field>
              <Field $full>
                <Label>Descri√ß√£o</Label>
                <TextArea
                  value={form.descricao}
                  onChange={e => setForm({ ...form, descricao: e.target.value })}
                />
              </Field>
              <Field $full>
                <Label>Hor√°rio de atendimento por dia</Label>
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: 8 }}>
                  {[
                    ['seg','Segunda'],['ter','Ter√ßa'],['qua','Quarta'],['qui','Quinta'],['sex','Sexta'],['sab','S√°bado'],['dom','Domingo']
                  ].map(([key,label]) => (
                    <div key={key} style={{ border: '1px solid #e5e7eb', borderRadius: 8, padding: 8 }}>
                      <div style={{ fontWeight: 600, marginBottom: 6 }}>{label}</div>
                      {(form.horario_atendimento[key] || []).map((it, idx) => (
                        <div key={idx} style={{ display: 'flex', alignItems: 'center', gap: 6, marginBottom: 6 }}>
                          <span>de</span>
                          <Input type="time" value={it.inicio} onChange={e => {
                            const arr = [...form.horario_atendimento[key]]; arr[idx] = { ...arr[idx], inicio: e.target.value };
                            setForm({ ...form, horario_atendimento: { ...form.horario_atendimento, [key]: arr } });
                          }} />
                          <span>at√©</span>
                          <Input type="time" value={it.fim} onChange={e => {
                            const arr = [...form.horario_atendimento[key]]; arr[idx] = { ...arr[idx], fim: e.target.value };
                            setForm({ ...form, horario_atendimento: { ...form.horario_atendimento, [key]: arr } });
                          }} />
                          <IconButton type="button" onClick={() => {
                            const arr = [...form.horario_atendimento[key]]; arr.splice(idx,1);
                            setForm({ ...form, horario_atendimento: { ...form.horario_atendimento, [key]: arr } });
                          }}>üóëÔ∏è</IconButton>
                        </div>
                      ))}
                      <SecondaryButton type="button" onClick={() => {
                        const arr = [...(form.horario_atendimento[key] || [])];
                        arr.push({ inicio: '08:00', fim: '12:00' });
                        setForm({ ...form, horario_atendimento: { ...form.horario_atendimento, [key]: arr } });
                      }}>Adicionar intervalo</SecondaryButton>
                    </div>
                  ))}
                </div>
              </Field>
              <Field>
                <Label>Valor consulta</Label>
                <Input
                  value={form.valor_consulta}
                  onChange={e => setForm({ ...form, valor_consulta: e.target.value })}
                />
              </Field>
              <Field>
                <Label>Dura√ß√£o (min)</Label>
                <Input
                  type="number"
                  value={form.tempo_consulta}
                  onChange={e => setForm({ ...form, tempo_consulta: e.target.value })}
                />
              </Field>
              <Field>
                <label style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                  <input
                    type="checkbox"
                    checked={form.aceita_convenio}
                    onChange={e => setForm({ ...form, aceita_convenio: e.target.checked })}
                  />
                  Aceita conv√™nio
                </label>
              </Field>
              <Field>
                <label style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                  <input
                    type="checkbox"
                    checked={form.gerenciar_agenda}
                    onChange={e => setForm({ ...form, gerenciar_agenda: e.target.checked })}
                  />
                  Gerenciar agenda
                </label>
              </Field>
              <ModalActions>
                <SecondaryButton type="button" onClick={() => setIsModalOpen(false)} disabled={saving}>Cancelar</SecondaryButton>
                <PrimaryButton type="submit" disabled={saving}>{saving ? 'Salvando...' : 'Salvar'}</PrimaryButton>
              </ModalActions>
            </Form>
          </ModalCard>
        </ModalOverlay>
      )}
    </Container>
  );
};

export default Especialistas;
