import React, { useEffect, useMemo, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import authService from '../services/authService';
import clientesService from '../services/clientesService';

const Container = styled.div`
  padding: 1rem 0;
`;

const Title = styled.h1`
  font-size: 1.6rem;
  color: #333;
  margin-bottom: 1rem;
`;

const Controls = styled.div`
  display: flex;
  gap: 12px;
  align-items: center;
  margin-bottom: 1rem;
`;

const SearchInput = styled.input`
  padding: 0.6rem 0.8rem;
  border: 2px solid #e1e5e9;
  border-radius: 8px;
  min-width: 280px;
`;

const CardGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, minmax(0, 1fr));
  gap: 12px;
  @media (max-width: 1200px) { grid-template-columns: repeat(2, minmax(0, 1fr)); }
  @media (max-width: 700px) { grid-template-columns: 1fr; }
`;

const Card = styled.div`
  background: white;
  border-radius: 10px;
  padding: 12px;
  box-shadow: 0 5px 20px rgba(0,0,0,0.08);
`;

const Row = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Name = styled.div`
  font-weight: 800;
  color: #111827;
`;

const Meta = styled.div`
  color: #6b7280;
  font-size: 0.9rem;
`;

const Badge = styled.span`
  display: inline-block;
  padding: 2px 8px;
  border-radius: 999px;
  font-size: 12px;
  font-weight: 700;
  color: ${p => (p.$ok ? '#065f46' : '#7c2d12')};
  background: ${p => (p.$ok ? '#d1fae5' : '#fee2e2')};
`;

const Empty = styled.div`
  padding: 1rem;
  color: #6b7280;
`;

const Cliente = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [search, setSearch] = useState('');
  const [modalOpen, setModalOpen] = useState(false);
  const [saving, setSaving] = useState(false);
  const [editing, setEditing] = useState(null); // { id_cliente, ... }
  const [form, setForm] = useState({ nome: '', contato: '', 'cpf-carteira': '', primeira_msg: true, convenio: false, id_convenio: '', data_nascimento: '' });
  const API_BASE_URL = process.env.REACT_APP_API_URL || '';

  useEffect(() => {
    (async () => {
      setLoading(true);
      setError('');
      try {
        const dominio = authService.getCurrentClient()?.dominio;
        const res = await axios.get(`${API_BASE_URL}/clientes`, { params: { dominio } });
        const data = res.data || {};
        if (!data.success) throw new Error(data.message || 'Falha ao listar clientes');
        setItems(Array.isArray(data.clientes) ? data.clientes : []);
      } catch (e) {
        setError(e.message || 'Erro ao carregar clientes');
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  const filtered = useMemo(() => {
    const q = search.trim().toLowerCase();
    if (!q) return items;
    return items.filter(c => String(c.nome || '').toLowerCase().includes(q) || String(c.contato || '').toLowerCase().includes(q));
  }, [items, search]);

  return (
    <Container>
      <Title>Clientes</Title>
      {error && <div style={{ background: '#fee', color: '#c33', padding: '8px 10px', borderRadius: 8, marginBottom: 10 }}>{error}</div>}
      <Controls>
        <SearchInput placeholder="Buscar por nome ou contato" value={search} onChange={e => setSearch(e.target.value)} />
        <button onClick={() => { setEditing(null); setForm({ nome: '', contato: '', 'cpf-carteira': '', primeira_msg: true, convenio: false, id_convenio: '', data_nascimento: '' }); setModalOpen(true); }} style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', color: 'white', padding: '0.6rem 0.9rem', borderRadius: 8, fontWeight: 700 }}>Novo cliente</button>
      </Controls>

      {loading ? (
        <Empty>Carregando...</Empty>
      ) : filtered.length === 0 ? (
        <Empty>Nenhum cliente encontrado.</Empty>
      ) : (
        <CardGrid>
          {filtered.map(c => (
            <Card key={c.id_cliente}>
              <Row>
                <Name>{c.nome}</Name>
                <Badge $ok={c.convenio === true}>{c.convenio ? 'Conv√™nio' : 'Particular'}</Badge>
              </Row>
              <Meta>{c.contato || '‚Äî'}</Meta>
              <Meta>CPF: {c['cpf-carteira'] || '‚Äî'}</Meta>
              <Meta>Primeira consulta: {c.primeira_msg ? 'Sim' : 'N√£o'}</Meta>
              <Meta>Data nascimento: {c.data_nascimento || '‚Äî'}</Meta>
              <div style={{ display: 'flex', gap: 8, marginTop: 8 }}>
                <button onClick={() => { setEditing(c); setForm({ nome: c.nome || '', contato: c.contato || '', 'cpf-carteira': c['cpf-carteira'] || '', primeira_msg: !!c.primeira_msg, convenio: !!c.convenio, id_convenio: c.id_convenio || '', data_nascimento: c.data_nascimento || '' }); setModalOpen(true); }} style={{ background: '#f3f4f6', padding: '6px 10px', borderRadius: 8 }}>‚úèÔ∏è Editar</button>
                <button onClick={async () => { if (!window.confirm('Excluir este cliente?')) return; try { await clientesService.remove(c.id_cliente); const rows = await clientesService.list(); setItems(rows); } catch (e) { alert(e.message || 'Erro ao excluir cliente'); } }} style={{ background: '#fee2e2', color: '#b91c1c', padding: '6px 10px', borderRadius: 8 }}>üóëÔ∏è Excluir</button>
              </div>
            </Card>
          ))}
        </CardGrid>
      )}

      {modalOpen && (
        <div onClick={(e) => { if (e.target === e.currentTarget) setModalOpen(false); }} style={{ position: 'fixed', inset: 0, background: 'rgba(0,0,0,.35)', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: 16, zIndex: 50 }}>
          <div style={{ background: 'white', width: '100%', maxWidth: 520, borderRadius: 12, padding: 16 }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 8 }}>
              <h3 style={{ margin: 0 }}>{editing ? 'Editar cliente' : 'Novo cliente'}</h3>
              <button onClick={() => setModalOpen(false)} style={{ background: '#f3f4f6', padding: '6px 10px', borderRadius: 8 }}>Fechar</button>
            </div>
            <div style={{ display: 'grid', gap: 10 }}>
              <label>
                <div style={{ color: '#374151', fontSize: 14, marginBottom: 6 }}>Nome</div>
                <input value={form.nome} onChange={e => setForm({ ...form, nome: e.target.value })} style={{ width: '100%', padding: '10px 12px', border: '2px solid #e5e7eb', borderRadius: 8 }} />
              </label>
              <label>
                <div style={{ color: '#374151', fontSize: 14, marginBottom: 6 }}>Contato</div>
                <input value={form.contato} onChange={e => setForm({ ...form, contato: e.target.value })} style={{ width: '100%', padding: '10px 12px', border: '2px solid #e5e7eb', borderRadius: 8 }} />
              </label>
              <label>
                <div style={{ color: '#374151', fontSize: 14, marginBottom: 6 }}>CPF-Carteira</div>
                <input value={form['cpf-carteira']} onChange={e => setForm({ ...form, 'cpf-carteira': e.target.value })} style={{ width: '100%', padding: '10px 12px', border: '2px solid #e5e7eb', borderRadius: 8 }} />
              </label>
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, minmax(0, 1fr))', gap: 10 }}>
                <label style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                  <input type="checkbox" checked={!!form.primeira_msg} onChange={e => setForm({ ...form, primeira_msg: e.target.checked })} /> Primeira msg
                </label>
                <label style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                  <input type="checkbox" checked={!!form.convenio} onChange={e => setForm({ ...form, convenio: e.target.checked })} /> Conv√™nio
                </label>
              </div>
              <label>
                <div style={{ color: '#374151', fontSize: 14, marginBottom: 6 }}>ID Conv√™nio</div>
                <input value={form.id_convenio} onChange={e => setForm({ ...form, id_convenio: e.target.value })} style={{ width: '100%', padding: '10px 12px', border: '2px solid #e5e7eb', borderRadius: 8 }} />
              </label>
              <label>
                <div style={{ color: '#374151', fontSize: 14, marginBottom: 6 }}>Data de Nascimento</div>
                <input type="date" value={form.data_nascimento} onChange={e => setForm({ ...form, data_nascimento: e.target.value })} style={{ width: '100%', padding: '10px 12px', border: '2px solid #e5e7eb', borderRadius: 8 }} />
              </label>
              <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 8 }}>
                <button onClick={() => setModalOpen(false)} style={{ background: '#f3f4f6', padding: '0.6rem 0.9rem', borderRadius: 8 }}>Cancelar</button>
                <button onClick={async () => {
                  setSaving(true);
                  try {
                    if (editing) await clientesService.update(editing.id_cliente, form); else await clientesService.create(form);
                    const rows = await clientesService.list();
                    setItems(rows);
                    setModalOpen(false);
                  } catch (e) {
                    alert(e.message || 'Erro ao salvar');
                  } finally {
                    setSaving(false);
                  }
                }} disabled={saving} style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', color: 'white', padding: '0.6rem 0.9rem', borderRadius: 8, fontWeight: 700 }}>{editing ? 'Salvar' : 'Criar'}</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </Container>
  );
};

export default Cliente;
