import React, { useEffect, useMemo, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import authService from '../services/authService';

const Container = styled.div`
  padding: 1rem 0;
`;

const Title = styled.h1`
  font-size: 1.6rem;
  color: #333;
`;

const CalendarGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 8px;
  margin-top: 12px;
`;

const DayCell = styled.div`
  background: #fff;
  border: 1px solid #e5e7eb;
  border-radius: 10px;
  padding: 8px;
  min-height: 120px;
  display: flex;
  flex-direction: column;
  gap: 6px;
`;

const DayHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-weight: 700;
  color: #374151;
`;

const EventTag = styled.div`
  padding: 4px 6px;
  border-radius: 8px;
  background: ${p => p.$bg || '#f3f4f6'};
  color: #111827;
  font-size: 12px;
  display: flex;
  align-items: center;
  gap: 6px;
  border: 1px solid #e5e7eb;
`;

const Toolbar = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  margin: 8px 0 12px 0;
`;

const Button = styled.button`
  background: #f3f4f6;
  color: #374151;
  padding: 6px 10px;
  border-radius: 8px;
`;

const Agendamento = () => {
  const [current, setCurrent] = useState(() => {
    const d = new Date();
    return new Date(d.getFullYear(), d.getMonth(), 1);
  });
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const API_BASE_URL = process.env.REACT_APP_API_URL || '';

  useEffect(() => {
    const load = async () => {
      setLoading(true);
      try {
        const dominio = authService.getCurrentClient()?.dominio;
        const res = await axios.get(`${API_BASE_URL}/agendamentos`, { params: { dominio } });
        const data = res.data || {};
        setItems(Array.isArray(data.agendamentos) ? data.agendamentos : []);
      } catch (e) {
        setItems([]);
      } finally {
        setLoading(false);
      }
    };
    load();
  }, [current]);

  const days = useMemo(() => {
    const year = current.getFullYear();
    const month = current.getMonth();
    const firstWeekDay = new Date(year, month, 1).getDay(); // 0-dom
    const startOffset = (firstWeekDay + 6) % 7; // começar na segunda
    const numDays = new Date(year, month + 1, 0).getDate();
    const arr = [];
    for (let i = 0; i < startOffset; i++) arr.push(null);
    for (let d = 1; d <= numDays; d++) arr.push(new Date(year, month, d));
    return arr;
  }, [current]);

  const eventsByDay = useMemo(() => {
    const map = {};
    for (const ev of items) {
      const dateStr = (ev.data_agendamento || ev.data || '').slice(0, 10);
      if (!dateStr) continue;
      if (!map[dateStr]) map[dateStr] = [];
      map[dateStr].push(ev);
    }
    return map;
  }, [items]);

  const nextMonth = () => setCurrent(new Date(current.getFullYear(), current.getMonth() + 1, 1));
  const prevMonth = () => setCurrent(new Date(current.getFullYear(), current.getMonth() - 1, 1));

  const monthLabel = current.toLocaleDateString('pt-BR', { month: 'long', year: 'numeric' });

  return (
    <Container>
      <Title>Agendamento</Title>
      <Toolbar>
        <Button onClick={prevMonth}>◀ Mês anterior</Button>
        <div style={{ fontWeight: 700, color: '#1f2937' }}>{monthLabel}</div>
        <Button onClick={nextMonth}>Próximo mês ▶</Button>
      </Toolbar>
      {loading ? (
        <div>Carregando...</div>
      ) : (
        <CalendarGrid>
          {['Seg','Ter','Qua','Qui','Sex','Sáb','Dom'].map(h => (
            <div key={h} style={{ fontWeight: 700, color: '#6b7280' }}>{h}</div>
          ))}
          {days.map((d, idx) => {
            const key = d ? d.toISOString().slice(0,10) : `empty-${idx}`;
            const dayEvents = d ? (eventsByDay[key] || []) : [];
            return (
              <DayCell key={key}>
                <DayHeader>
                  <span>{d ? d.getDate() : ''}</span>
                </DayHeader>
                <div style={{ display: 'grid', gap: 4 }}>
                  {dayEvents.slice(0, 5).map(ev => (
                    <EventTag key={`${ev.id_agendamento}-${ev.horario}`} $bg={(ev.cor || '').replace('rgb', 'rgba').replace(')', ',0.18)')}>
                      <span style={{ width: 8, height: 8, borderRadius: 999, background: ev.cor || '#9ca3af' }} />
                      <span style={{ fontWeight: 700 }}>{(ev.horario || '').toString().slice(0,5)}</span>
                      <span style={{ color: '#374151' }}>{ev.nome_especialista || 'Especialista'}</span>
                    </EventTag>
                  ))}
                  {dayEvents.length > 5 && (
                    <span style={{ color: '#6b7280', fontSize: 12 }}>+{dayEvents.length - 5} mais</span>
                  )}
                </div>
              </DayCell>
            );
          })}
        </CalendarGrid>
      )}
    </Container>
  );
};

export default Agendamento;
